
public class APstuff {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int total = 0;

		for (int k = 0; k <= 100; k += 2)

		{

			total += k;

		}
		System.out.println(total);

		for (int k = 0; k < 101; k += 2)

		{

			total += k - 1;

		}
		System.out.println(total);

	}
}
